import Link from 'next/link';
import { GetStaticProps, InferGetStaticPropsType } from 'next'
import db from "../src/firebase";
import withLayout from "../components/Layout";
import styled from 'styled-components';

const StyledTitle = styled.div`
  margin-top: 20px;
  font-size: 60px;
  font-weight: 900;

  .auther {
    font-size: 20px; 
    font-weight: 300;
  }

  .subtitle {
    margin-top: 60px;
    font-size: 25px;
    font-weight: 700;
  }
`

const StyledDiscription = styled.div`
  max-width: 600px;
  margin-top: 40px;
  font-size: 20px;
  font-weight: 400px;
`

const Index = (props: any) => (
    <div>
        <StyledTitle>
          Development Footprints
          <h3 className="auther">
            by Jiin Kim
          </h3>
          <h2 className="subtitle">
            Sharing thoughts and inspiration
          </h2>
        </StyledTitle>

        <StyledDiscription>
          <p>
          My name is Jiin Kim.
I'm excited by an opportunity to diving into a new world and mingling with new people. A strong believer of that experiences and challenges are the only key a person can grow.

The fast-paced tech world requires a programmer to know how to learn and absorb through self-taught. I'm familiar with startup environment where heavy loads of responsibilities are naturally demanded.
          </p>
        </StyledDiscription>

        <style jsx global>{`
          html,
          body {
            font-family: Roboto;
            --primary-color: #3f0f40;
          }
        `}</style>
    </div>
)

