import { SvgIconProps } from '@material-ui/core';
import styled from "styled-components";

const styleIcon = (Icon: (props: SvgIconProps) => JSX.Element) => styled(Icon)`
    padding: 10px;
    color: white;
    font-size: 20px;
`

type SidebarOptionProps = {
    Icon?: (props: SvgIconProps) => JSX.Element;
    title: string
}

const SidebarOption = ({Icon, title}: SidebarOptionProps) => {
    const StyledIcon = Icon ? styleIcon(Icon) : null;
    return (
        <div className="sidebarOption">
            {StyledIcon && <StyledIcon />}
            {StyledIcon ? (
                <h3>{title}</h3>
            ): (
                <h3 className="sidebarOption__channel">
                    <span className="sidebarOption__hash">{title}</span>
                </h3>
            )}
            <style jsx>{`
                .sidebarOption {
                    display: flex;
                    aligin-items: center;
                    font-size: 12px;
                    padding-left: 2px;
                    cursor: pointer;
                }
                .sidebarOption:hover {
                    opacity: 0.9;
                    background-color: #340e36;
                }
                .sidebarOption__channel {
                    padding: 10px 0;
                }
                .sidebarOption > h3 {
                    font-weight: 500;
                }
            `}</style>
        </div>
    )
}

export default SidebarOption;
