import { SvgIconProps } from '@material-ui/core';
import FiberManualRecordIcon from "@material-ui/icons/FiberManualRecord";
import CreateIcon from "@material-ui/icons/Create";
import styled from "styled-components";
import SidebarOption from "./SidebarOption";
import InsertCommentIcon from "@material-ui/icons/InsertComment";
import InboxIcon from "@material-ui/icons/Inbox";
import DraftsIcon from "@material-ui/icons/Drafts";

const StyledSidebar = styled.div`
    color: white;
    background-color: var(--primary-color);
    border-top: 1px solid #49274b;
    max-width: 260px;

    > hr {
        margin-top: 10px;
        margin-bottom: 10px;
        border: 1px solid #49274b;      
    }
`
const StyledSidebarHeader = styled.div`
    display: flex;
    border-bottom: 1px solid #49274b;
    padding-bottom: 10px;
    padding: 13px;

    .info {
        flex: 1;
    }
    .info > h2 {
        font-size: 15px;
        font-weight: 900;
        margin-bottom: 5px;
    }
    .info > h3 {
        display: flex;
        align-items: center;
        font-size: 13px;
        font-weight: 400;       
    }
    .icon .activityIcon {
        font-size: 14px;
        margin-top: 1px;
        margin-right: 2px;
        color: green;       
    }
`

const StyledCreateIcon = styled(CreateIcon)`
    padding: 8px;
    color: #49274b;
    font-size: 35px;
    background-color: white;
    border-radius: 999px;
`

const StyledFiberManualRecordIcon = styled(FiberManualRecordIcon) `
    font-size: 14px;
    margin-top: 1px;
    margin-right: 2px;
    color: green;
`

const StyledSidebarIcon = (Icon: (props: SvgIconProps) => JSX.Element) => styled(Icon)`
    padding: 10px;
    color: white;
    font-size: 20px;
`

const Sidebar = () => (
    <StyledSidebar>
        <StyledSidebarHeader>
            <div className="info">
                <h2>My Portfolio</h2>
                <h3>
                    <FiberManualRecordIcon className="activityIcon" />
                    Jiin Kim
                </h3>
            </div>
            <CreateIcon />
        </StyledSidebarHeader>

        <SidebarOption Icon={StyledSidebarIcon(InsertCommentIcon)} title="Threads"/>
        <SidebarOption Icon={StyledSidebarIcon(InboxIcon)} title="Youtube"/>
        <SidebarOption Icon={StyledSidebarIcon(DraftsIcon)} title="Draft"/>
        <SidebarOption Icon={StyledSidebarIcon(InsertCommentIcon)} title="Channel"/>
        <SidebarOption Icon={StyledSidebarIcon(InsertCommentIcon)} title="General"/>
        <SidebarOption Icon={StyledSidebarIcon(DraftsIcon)} title="Threads"/>
        <SidebarOption Icon={StyledSidebarIcon(InsertCommentIcon)} title="Threads"/>
        <SidebarOption Icon={StyledSidebarIcon(InboxIcon)} title="Inbox"/>
        <hr />
        <SidebarOption Icon={StyledSidebarIcon(InsertCommentIcon)} title="Threads"/>
        <SidebarOption Icon={StyledSidebarIcon(InsertCommentIcon)} title="Threads"/>
    </StyledSidebar>
)

export default Sidebar;
