import Link from 'next/link';
import { GetStaticProps, InferGetStaticPropsType } from 'next'
import db from "../src/firebase";
import withLayout from "../components/Layout";
import styled from 'styled-components';

const StyledTitle = styled.div`
  margin-top: 20px;
  font-size: 60px;
  font-weight: 900;

  .auther {
    font-size: 30px; 
    font-weight: 300;
  }

  .subtitle {
    margin-top: 100px;
    font-size: 25px;
    font-weight: 700;
  }
`

const StyledDiscription = styled.div`
  max-width: 600px;
  margin-top: 40px;
  font-size: 20px;
  font-weight: 400px;
`

const StyledSection = styled.div`
  margin-top: 100px;
  margin-bottom: 100px;

  > hr {
    margin-bottom: 30px;
  }
`

const Index = (props: any) => (
    <div>
        <StyledTitle>
          Development Footprints
          <h3 className="auther">
            by Jiin Kim
          </h3>
          <h2 className="subtitle">
            Sharing thoughts and inspiration
          </h2>
        </StyledTitle>

        <StyledDiscription>
          <p>
          I'm a Software Developer who resides in Vancouver, Canada. I love coding, and meeting new people with such creative ideas and inspiration. 

          <br /><br />
          Contact me if you're intriguted with my posts or projects!
          </p>
        </StyledDiscription>

        <StyledSection>
          <hr />
          <h2>Personal Projects</h2>
        </StyledSection>

        <style jsx global>{`
          html,
          body {
            font-family: Roboto;
            --primary-color: #3f0f40;
          }
        `}</style>
    </div>
)

