import Link from 'next/link';
import { GetStaticProps, InferGetStaticPropsType } from 'next'
import db from "../src/firebase";
import withLayout from "../components/Layout";
import styled from 'styled-components';

const StyledTitle = styled.div`
  margin-top: 20px;
  font-size: 60px;
  font-weight: 900;

  .auther {
    font-size: 30px; 
    font-weight: 300;
  }

  .subtitle {
    margin-top: 60px;
    font-size: 25px;
    font-weight: 700;
  }
`

const StyledDiscription = styled.div`
  max-width: 600px;
  margin-top: 40px;
  font-size: 20px;
  font-weight: 400px;
`

const Index = (props: any) => (
    <div>
        <StyledTitle>
          Development Footprints
          <h3 className="auther">
            by Jiin Kim
          </h3>
          <h2 className="subtitle">
            Sharing thoughts and inspiration
          </h2>
        </StyledTitle>

        <StyledDiscription>
          <p>
          I am a Software Developer resides in Vancouver, Canada. I'm excited by an opportunity to diving into a new world and meeting new people, 
          a strong believer who values experiences and challenges as the key of growing as a technician.
          <br />
          </p>
        </StyledDiscription>

        <style jsx global>{`
          html,
          body {
            font-family: Roboto;
            --primary-color: #3f0f40;
          }
        `}</style>
    </div>
)

