import FiberManualRecordIcon from "@material-ui/icons/FiberManualRecord";
import CreateIcon from "@material-ui/icons/Create";
import styled from "styled-components";
import SidebarOption from "./SidebarOption";
import InsertCommentIcon from "@material-ui/icons/InsertComment";

const StyledCreateIcon = styled(CreateIcon)`
    padding: 8px;
    color: #49274b;
    font-size: 35px;
    background-color: white;
    border-radius: 999px;
`

const StyledFiberManualRecordIcon = styled(FiberManualRecordIcon) `
    font-size: 14px;
    margin-top: 1px;
    margin-right: 2px;
    color: green;
`

const StyledIcon = styled(InsertCommentIcon)`
    padding: 10px;
    color: white;
    font-size: 20px;
`

const Sidebar = () => (
    <div className="sidebar">
        <div className="sidebar__header">
            <div className="sidebar__info">
                <h2>My Portfolio</h2>
                <h3>
                    <StyledFiberManualRecordIcon />
                    Jiin Kim
                </h3>
            </div>
            <StyledCreateIcon />
        </div>
        <SidebarOption Icon={StyledIcon} title="Threads"/>
        <SidebarOption title="# Youtube"/>
        <style jsx>{`
            .sidebar {
                color: white;
                background-color: var(--primary-color);
                border-top: 1px solid #49274b;
                max-width: 260px;
            }
            .sidebar__header {
                display: flex;
                border-bottom: 1px solid #49274b;
                padding-bottom: 10px;
                padding: 13px;
            }
            .sidebar__info {
                flex: 1;
            }
            .sidebar__info > h2 {
                font-size: 15px;
                font-weight: 900;
                margin-bottom: 5px;
            }
            .sidebar__info > h3 {
                display: flex;
                align-items: center;
                font-size: 13px;
                font-weight: 400;
            }
        `}</style>
    </div>
)

export default Sidebar;
