  //const res = await fetch('https://.../posts')
  //const posts = await res.json()
  const channels: Array<object> = [];
  await db.collection("room").get()
      .then(snapshot => snapshot.forEach(doc => {
          channels.push({
              id: doc.id,
              name: doc.data().name
          })
      }))
  return {
    props: {
      channels,
    },
  }
}
*/

export default withLayout(Index);
